class Singleton {
  static instance;
  constructor(name) {
    if (Singleton.instance) {
      return Singleton.instance;
    }
    this.name = name;
    Singleton.instance = this;
  }
}

const a = new Singleton('First');
const b = new Singleton('Second');
console.log(a === b); // true
console.log(a.name); // 'First'
console.log(b.name); // 'First'

class Creator {
  static createObject(type) {
    if (type === 'type1') {
      return new Type1Class();
    } else if (type === 'type2') {
      return new Type2Class();
    }
  }
}

class Type1Class {
  operation() {
    return 'Result from Type1Class';
  }
}

class Type2Class {
  operation() {
    return 'Result from Type2Class';
  }
}

const obj1 = Creator.createObject('type1');
console.log(obj1.operation()); // 'Result from Type1Class'
const obj2 = Creator.createObject('type2');
console.log(obj2.operation()); // 'Result from Type2Class'

class Subject {
  constructor() {
    this.observers = [];
  }

  subscribe(observer) {
    this.observers.push(observer);
  }

  unsubscribe(observer) {
    this.observers = this.observers.filter(obs => obs !== observer);
  }

  notify(data) {
    this.observers.forEach(observer => observer.update(data));
  }
}

class Observer {
  update(data) {
    console.log('Observer received data:', data);
  }
}

const subject = new Subject();
const observer = new Observer();
subject.subscribe(observer);
subject.notify('new data'); // Observer received data: new data

